    public static void handleItemTransfer() {
        final int emptySlot;
        if (Module.mc.player.inventory.getItemStack().getItem() != Items.AIR && (emptySlot = findEmptySlot(false)) != -1) {
            Module.mc.playerController.windowClick(0, emptySlot, 0, ClickType.PICKUP, Module.mc.player);
        }
        Module.mc.player.closeScreen();
    }
    
    public static void handleClick(final EventWindowClick eventWindowClick) {
        final boolean sneaking = Module.mc.player.isSneaking();
        if (eventWindowClick.getClickStage() == EventWindowClick.ClickStage.PRE) {
            Module.mc.player.setSprinting(true);
            if (!sneaking) {
                return;
            }
            Module.mc.player.connection.sendPacket(new CEntityActionPacket(Module.mc.player, CEntityActionPacket.Action.RELEASE_SHIFT_KEY));
        }
        if (eventWindowClick.getClickStage() == EventWindowClick.ClickStage.POST) {
            if (!sneaking) {
                return;
            }
            Module.mc.player.connection.sendPacket(new CEntityActionPacket(Module.mc.player, CEntityActionPacket.Action.PRESS_SHIFT_KEY));
        }
    }
    
