    public static Color interpolateColorC(final int n, final int n2, float min) {
        min = Math.min(1.0f, Math.max(0.0f, min));
        return new Color(interpolateInt(getRed(n), getRed(n2), min), interpolateInt(getGreen(n), getGreen(n2), min), interpolateInt(getBlue(n), getBlue(n2), min), interpolateInt(getAlpha(n), getAlpha(n2), min));
    }
    
    public static int interpolateInt(final int n, final int n2, final double n3) {
        return interpolate(n, n2, (float)n3).intValue();
    }
    
    public static Double interpolate(final double n, final double n2, final double n3) {
        return n + (n2 - n) * n3;
    }
    
    public static int getRed(final int n) {
        return n >> 16 & 0xFF;
    }
    
    public static int getGreen(final int n) {
        return n >> 8 & 0xFF;
    }
    
    public static int getBlue(final int n) {
        return n & 0xFF;
    }
    
    public static int getAlpha(final int n) {
        return n >> 24 & 0xFF;
    }
    
